import React, { useState } from 'react';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';

function ClientForm() {
 const [nome, setNome] = useState('');
 const [email, setEmail] = useState('');
 const [telefone, setTelefone] = useState('');
 const [x, setX] = useState('');
 const [y, setY] = useState('');

 const handleChange = event => {
    const { name, value } = event.target;
    switch (name) {
      case 'nome':
        setNome(value);
        break;
      case 'email':
        setEmail(value);
        break;
      case 'telefone':
        setTelefone(value);
        break;
      case 'x':
        setX(value);
        break;
      case 'y':
        setY(value);
        break;
      default:
        break;
    }
 };

 const handleSubmit = event => {
    event.preventDefault();
    axios.post('http://localhost:5000/api/clientes', { nome, email, telefone, x, y })
      .then(res => {
        console.log(res.data);
        // Redirecionar ou atualizar a lista de clientes após o cadastro
      });
 };

 return (
    <Form onSubmit={handleSubmit}>
      <Form.Group controlId="formNome">
        <Form.Label>Nome</Form.Label>
        <Form.Control type="text" name="nome" value={nome} onChange={handleChange} placeholder="Nome" />
      </Form.Group>

      <Form.Group controlId="formEmail">
        <Form.Label>Email</Form.Label>
        <Form.Control type="email" name="email" value={email} onChange={handleChange} placeholder="Email" />
      </Form.Group>

      <Form.Group controlId="formTelefone">
        <Form.Label>Telefone</Form.Label>
        <Form.Control type="text" name="telefone" value={telefone} onChange={handleChange} placeholder="Telefone" />
      </Form.Group>

      <Form.Group controlId="formX">
        <Form.Label>Coordenada X</Form.Label>
        <Form.Control type="text" name="x" value={x} onChange={handleChange} placeholder="Coordenada X" />
      </Form.Group>

      <Form.Group controlId="formY">
        <Form.Label>Coordenada Y</Form.Label>
        <Form.Control type="text" name="y" value={y} onChange={handleChange} placeholder="Coordenada Y" />
      </Form.Group>

      <Button variant="primary" type="submit">
        Cadastrar
      </Button>
    </Form>
 );
}

export default ClientForm;
