import React, { useState } from 'react';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';


function ClientFilter() {
 const [filter, setFilter] = useState('');
 const [filteredClients, setFilteredClients] = useState([]);
 const [name, setName] = useState('');

 const handleChange = event => {
    const {value} = event.target;
    setFilter(event.target.value);
    setName(value)
 };

 const handleSubmit = event => {
    event.preventDefault();
    axios.get(`http://localhost:5000/api/clientes/filtrar?nome=${filter}`)
      .then(res => {
        setFilteredClients(res.data);
      });
 };

 return (
    <div>
      <Form.Group onSubmit={handleSubmit}>
      <Form.Control type="text" name="name" value={name} onChange={handleChange} placeholder="Filtrar por nome" />
      <Button>Filtrar</Button>
      </Form.Group>
      
      <ul>
        {filteredClients.map(client => (
          <li key={client.id}>{client.nome} - {client.email}</li>
        ))}
      </ul>
    </div>
 );
}

export default ClientFilter;
