import React, { useState } from 'react';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';


function ClientForm() {
 const [name, setName] = useState('');
 const [email, setEmail] = useState('');
 const [phone, setPhone] = useState('');
 const [x, setX] = useState('');
 const [y, setY] = useState('');

 const handleChange = event => {
    const { name, value } = event.target;
    switch (name) {
      case 'nome':
        setName(value);
        break;
      case 'email':
        setEmail(value);
        break;
      case 'phone':
      value = value.replace(/\D/g, ''); // Remove todos os caracteres não numéricos
      value = value.replace(/(\d{2})(\d)/, '($1) $2'); // Formata o código de área
      value = value.replace(/(\d{5})(\d)/, '$1-$2'); // Formata o número de telefone
      setPhone(value);
        break;
      case 'x':
        setX(value);
        break;
      case 'y':
        setY(value);
        break;
      default:
        break;
    }
 };

 const handleSubmit = event => {
  event.preventDefault();

  if (isNaN(parseFloat(x)) || isNaN(parseFloat(y))) {
    alert('Por favor, insira números válidos para X e Y.');
    return;
 }

  const xNumber = parseFloat(x);
  const yNumber = parseFloat(y);

  

  axios.post('http://localhost:5000/api/clientes', { name, email, phone, x: xNumber, y: yNumber })
     .then(res => {
       console.log(res.data);
       // Aqui você pode adicionar lógica para lidar com a resposta do servidor,
       // por exemplo, atualizar a lista de clientes ou mostrar uma mensagem de sucesso
     })
     .catch(error => {
       if (error.response) {
         // O servidor retornou uma resposta com status de erro
         console.error(error.response.data.message);
         // Aqui você pode mostrar a mensagem de erro no frontend
       } else {
         // Algo aconteceu na requisição que não foi capturado pelo servidor
         console.error('Erro ao cadastrar cliente.');
       }
     });
 };

 return (
  <div className='controle-formulario'>
    <Form onSubmit={handleSubmit}>
      <Form.Group controlId="formNome">
        <Form.Label>Nome</Form.Label>
        <Form.Control type="text" name="nome" value={name} onChange={handleChange} placeholder="Nome" />
      </Form.Group>

      <Form.Group controlId="formEmail">
        <Form.Label>Email</Form.Label>
        <Form.Control type="email" name="email" value={email} onChange={handleChange} placeholder="Email" />
      </Form.Group>

      <Form.Group controlId="formTelefone">
        <Form.Label>Telefone</Form.Label>
        <Form.Control type="text" name="phone" value={phone} onChange={handleChange} placeholder="Telefone" />
      </Form.Group>

      <Form.Group controlId="formX">
      <Form.Label>Coordenada X</Form.Label>
      <Form.Control type="number" step="0.01" name="x" value={x} onChange={handleChange} placeholder="Coordenada X" />
      </Form.Group>

      <Form.Group controlId="formY">
      <Form.Label>Coordenada Y</Form.Label>
      <Form.Control type="number" step="0.01" name="y" value={y} onChange={handleChange} placeholder="Coordenada Y" />
      </Form.Group>

      <Button variant="primary" type="submit">
        Cadastrar
      </Button>
      
    </Form>
    </div>
 );
}

export default ClientForm;
