import React, { useState } from 'react';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';


function ClientFilter() {
 const [filteredClients, setFilteredClients] = useState([]);
 const [name, setName] = useState('');

 const handleChange = event => {
    const {value} = event.target;
    setName(value)
 };

 const handleSubmit = event => {
    event.preventDefault();
    axios.get(`http://localhost:5000/api/clientes/filtrar?nome=${name}`)
      .then(res => {
        setFilteredClients(res.data);
      });
 };

 return (
    <div className='container-filter'>
      <Form.Group onSubmit={handleSubmit} className='filtragem-cliente'>
      <Form.Control type="text" name="name" value={name} onChange={handleChange} placeholder="Filtrar por nome" />
      <Button>Filtrar</Button>
      </Form.Group>
      <ul>
        {filteredClients.map(client => (
          <li key={client.id}>{client.nome} - {client.email}</li>
        ))}
      </ul>
    </div>
 );
}

export default ClientFilter;
