import React, { useState } from 'react';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';


function ClientFilter() {
 const [filteredClients, setFilteredClients] = useState([]);
 const [name, setName] = useState('');
 const [email, setEmail] = useState('');
 const [phone, setPhone] = useState('');

 const handleChange = event => {
    const {name ,value} = event.target;
    switch (name) {
      case 'name':
        setName(value);
        break;
        case 'phone':
          setPhone(value);
          break;
      case 'email':
        setEmail(value);
        break;

        default:
          break;
    }
 };

 const handleSubmit = event => {
    event.preventDefault();
    axios.get(`http://localhost:5000/api/clientes/filtrar?name=${name}&phone=${phone}&email=${email}`)
      .then(res => {
        setFilteredClients(res.data);
      });
 };

 return (
    <div className='container-filter'>
      <Form.Group onSubmit={handleSubmit} className='filtragem-cliente'>
      <Form.Control type="text" name="name" value={name} onChange={handleChange} placeholder="Nome para filtragem" />
      <Form.Control type="tel" name="phone" value={phone} onChange={handleChange} placeholder="Telefone para filtragem" />
      <Form.Control type="text" name="email" value={email} onChange={handleChange} placeholder="Email para filtragem" />
      <Button>Filtrar</Button>
      </Form.Group>
      <ul>
        {filteredClients.map(client => (
          <li key={client.id}>{client.nome} - {client.email}</li>
        ))}
      </ul>
    </div>
 );
}

export default ClientFilter;
