import React, { useState } from 'react';
import axios from 'axios';

function ClientForm() {
 const [nome, setNome] = useState('');
 const [email, setEmail] = useState('');
 const [telefone, setTelefone] = useState('');
 const [x, setX] = useState('');
 const [y, setY] = useState('');

 const handleChange = event => {
    const { name, value } = event.target;
    switch (name) {
      case 'nome':
        setNome(value);
        break;
      case 'email':
        setEmail(value);
        break;
      case 'telefone':
        setTelefone(value);
        break;
      case 'x':
        setX(value);
        break;
      case 'y':
        setY(value);
        break;
      default:
        break;
    }
 };

 const handleSubmit = event => {
    event.preventDefault();
    axios.post('http://localhost:5000/api/clientes', { nome, email, telefone, x, y })
      .then(res => {
        console.log(res.data);
        
      });
 };

 return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="nome" value={nome} onChange={handleChange} placeholder="Nome" />
      <input type="email" name="email" value={email} onChange={handleChange} placeholder="Email" />
      <input type="text" name="telefone" value={telefone} onChange={handleChange} placeholder="Telefone" />
      <input type="text" name="x" value={x} onChange={handleChange} placeholder="Coordenada X" />
      <input type="text" name="y" value={y} onChange={handleChange} placeholder="Coordenada Y" />
      <button type="submit">Cadastrar</button>
    </form>
 );
}

export default ClientForm;
