import React, { useState } from 'react';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';


function CordinateView() {
 const [filteredClients, setFilteredClients] = useState([]);
 const [clienteId, setClienteId] = useState('');
 const handleChange = event => {
    const {value} = event.target;
    setClienteId(value)
    }


 const handleSubmit = event => {
    event.preventDefault();
    axios.get(`http://localhost:5000/api/coordenadas/filtrar?clienteId=${clienteId}`)
      .then(res => {
        setFilteredClients(res.data);
      });
 };

 return (
    <div className='container-filter'>
      <Form onSubmit={handleSubmit}>
      <Form.Group className='filtragem-cliente'>
      <Form.Control type="text" name="name" value={clienteId} onChange={handleChange} placeholder="Nome para filtragem" />
      <Button variant="primary" type="submit">Filtrar coordenada</Button>
      </Form.Group>
      </Form>
      <ul>
        {filteredClients.map(cliente => (          
          <li key={cliente.id}>nome : {cliente.name} - telefone : {cliente.phone} - email :{cliente.email}</li>
        ))}
      </ul>
    </div>
 );

};
export default CordinateView;
